mapper.update:
UPDATE PLAN


		PLAN_TITLE = #{plan_title}


		PLAN_SUBTITLE = #{plan_subTitle}


		PLAN_IMG = #{plan_img}


		PLAN_CONTENT = #{plan_content}


		PLAN_PLACE = #{plan_place}


		PLAN_DATE = #{plan_date}


		PLAN_COUNT = #{plan_count}


		PLAN_REGDATE = #{plan_regDate}


WHERE PLAN_PK = #{plan_pk} AND PLAN_WRITER = #{plan_writer}
